import React, { useState } from "react";
import {
  Avatar, Button, CssBaseline, TextField, Link, Grid,
  Box, Typography, Container
} from "@mui/material";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { useNavigate } from "react-router-dom";

const theme = createTheme();

export default function SignUp({ users, setUsers }) {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    type: "Student",
    status: "active"
  });

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const newUser = {
      id: users.length + 1,
      ...formData,
    };

    setUsers([...users, newUser]);

    // If you want to persist this in `Data.json`, you would need a backend.
    // Without a backend, you can only keep this in state or localStorage.
    alert("User registered successfully");
    navigate("/");
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{ marginTop: 8, display: "flex", flexDirection: "column", alignItems: "center" }}
        >
          <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign up
          </Typography>
          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField name="name" required fullWidth label="Full Name" onChange={handleChange} />
              </Grid>
              <Grid item xs={12}>
                <TextField name="email" required fullWidth label="Email" onChange={handleChange} />
              </Grid>
              <Grid item xs={12}>
                <TextField name="password" type="password" required fullWidth label="Password" onChange={handleChange} />
              </Grid>
            </Grid>
            <Button type="submit" fullWidth variant="contained" sx={{ mt: 3, mb: 2 }}>
              Sign Up
            </Button>
            <Grid container justifyContent="flex-end">
              <Grid item>
                <Link href="/" variant="body2">
                  Already have an account? Sign in
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
}
