import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const DeletePost = ({ data }) => {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPosts = async () => {
      const fetchedPosts = await data.getPosts(); // Assuming this calls backend
      setPosts(fetchedPosts);
    };

    fetchPosts();
  }, [data]);

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/deletepost/${id}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        throw new Error("Failed to delete post");
      }

      // Update the posts in state
      setPosts((prevPosts) => prevPosts.filter((post) => post.id !== id));

      // Optional: navigate back or show a toast
      navigate("/"); // or stay on the same page
    } catch (error) {
      console.error("Error deleting post:", error);
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "0 auto", padding: "20px" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>All Posts</h2>
      {posts.map((post) => (
        <div key={post.id} style={{ marginBottom: "20px" }}>
          <h3 style={{ marginBottom: "10px", color: "#333" }}>{post.title}</h3>
          <p style={{ marginBottom: "10px" }}>{post.body}</p>
          <button
            onClick={() => handleDelete(post.id)}
            style={{
              backgroundColor: "#f44336",
              color: "#fff",
              padding: "8px 16px",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              fontSize: "14px",
            }}
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

export default DeletePost;
