import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Button, TextField } from "@mui/material";

function PostinDetail(props) {
  const { postId } = useParams();
  const { data } = props;
  const [reload, setReload] = useState(false);
  const [comments, setComments] = useState([]);
  const [posts, setPosts] = useState(null);
  const [post, setPost] = useState(null);
  const [reply, setReply] = useState("");
  const [summary, setSummary] = useState("");
  const [showAdditionalInfo, setShowAdditionalInfo] = useState(false);
  const loggedIn = localStorage.getItem("loggedIn");
  const userName = localStorage.getItem("userName");

  const fetchPosts = async () => {
    try {
      const fetchedPosts = await data.getPosts();
      setPosts(fetchedPosts);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, [data]);

  useEffect(() => {
    const selectedPost = posts?.find((p) => String(p._id) === String(postId));
    setPost(selectedPost);
    if (selectedPost) {
      setComments(selectedPost.comments || []);
    }
  }, [posts, postId]);

  const handleClick = async () => {
    if (!reply.trim()) return;

    const comment = {
      postId,
      author: userName,
      text: reply,
    };

    try {
      const response = await fetch("http://localhost:5000/api/add-comment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(comment),
      });

      if (!response.ok) throw new Error("Failed to add comment");

      const result = await response.json();
      setComments(result.post.comments);
      setReply("");
    } catch (error) {
      console.error("Error posting comment:", error);
    }
  };

  const handleFillTextField = async () => {
    setShowAdditionalInfo(!showAdditionalInfo);
    if (!showAdditionalInfo && post) {
      try {
        const response = await fetch("http://localhost:5000/api/generate-reply", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ post }),
        });

        if (!response.ok) {
          throw new Error("Failed to generate reply");
        }

        const data = await response.json();
        setReply(data.generatedReply);

        // Save AI-generated reply to database
        const autoComment = {
          postId,
          author: "ChatGPT",
          text: data.generatedReply,
        };

        const saveResponse = await fetch("http://localhost:5000/api/add-comment", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(autoComment),
        });

        if (!saveResponse.ok) throw new Error("Failed to save AI reply");
        const saveResult = await saveResponse.json();
        setComments(saveResult.post.comments);
      } catch (error) {
        console.error("Error generating/saving AI reply:", error);
      }
    } else {
      setReply("");
    }
  };

  const handleGetSummary = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/get-summary", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ post }),
      });

      if (!response.ok) {
        throw new Error("Failed to get summary from backend");
      }

      const result = await response.json();
      setSummary(result.summary);
    } catch (error) {
      console.error("Error getting summary:", error);
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "0 auto", padding: "20px", fontFamily: "Arial, sans-serif" }}>
      {post ? (
        <>
          <h1 style={{ fontSize: "24px", marginBottom: "10px" }}>{post.title}</h1>
          <h2 style={{ fontSize: "18px", marginBottom: "5px" }}>Author: {post.author}</h2>
          <h3 style={{ fontSize: "16px", fontStyle: "italic", marginBottom: "0" }}>Topic: {post.topic}</h3>
          <p style={{ fontSize: "16px", lineHeight: "1.6", marginBottom: "20px" }}>{post.body}</p>
        </>
      ) : (
        <p>Loading...</p>
      )}

      {loggedIn && (
        <>
          <TextField
            label="Enter reply"
            variant="outlined"
            fullWidth
            value={reply}
            onChange={(e) => setReply(e.target.value)}
            style={{ marginBottom: "10px" }}
          />
          <Button
            style={{
              backgroundColor: "#4caf50",
              color: "white",
              borderRadius: "5px",
              padding: "10px 20px",
              fontSize: "16px",
              fontWeight: "bold",
              border: "none",
              cursor: "pointer",
              marginRight: "10px",
            }}
            onClick={handleClick}
          >
            Add a reply
          </Button>
          <Button
            style={{
              backgroundColor: "#007bff",
              color: "white",
              borderRadius: "5px",
              padding: "10px 20px",
              fontSize: "16px",
              fontWeight: "bold",
              border: "none",
              cursor: "pointer",
              marginRight: "10px",
            }}
            onClick={handleFillTextField}
          >
            {showAdditionalInfo ? "Disable reply by ChatGPT" : "Generate reply by ChatGPT"}
          </Button>
          <Button
            style={{
              backgroundColor: "#6c63ff",
              color: "white",
              borderRadius: "5px",
              padding: "10px 20px",
              fontSize: "16px",
              fontWeight: "bold",
              border: "none",
              cursor: "pointer",
              marginTop: "10px",
            }}
            onClick={handleGetSummary}
          >
            Get summary using OpenAI
          </Button>
        </>
      )}

      <h2 style={{ fontSize: "20px", marginBottom: "10px", marginTop: "30px" }}>Reply Section</h2>
      <div style={{ backgroundColor: "#f0f0f0", padding: "10px", marginBottom: "10px", borderRadius: "5px" }}>
        {comments?.map((comment, index) => (
          <div key={index} style={{ marginBottom: "10px" }}>
            <p style={{ margin: "0", padding: "5px 0", fontWeight: "bold" }}>{comment.text}</p>
            <p style={{ margin: "0", padding: "5px 0", color: "#888" }}>Author: {comment.author}</p>
          </div>
        ))}
      </div>

      {summary && (
        <>
          <h2 style={{ fontSize: "20px", marginTop: "30px" }}>Post Summary</h2>
          <div
            style={{
              backgroundColor: "#e0e0ff",
              padding: "10px",
              borderRadius: "5px",
              fontSize: "16px",
              lineHeight: "1.6",
            }}
          >
            {summary}
          </div>
        </>
      )}
    </div>
  );
}

export default PostinDetail;
